sys_variable_value.value:
(function execute(inputs, outputs) {
    outputs.email_var = new CF_EmailParsingUtils(inputs.debug).parseNameValuePairs(inputs.emailBodyText); //return parsed name/val pairs
})(inputs, outputs);

